import React,{useState} from 'react'

const Text = (props) => {
    const [text, setText] = useState("")

    const handleOnChange=(e)=>{
        setText(e.target.value);
    }

    const changeToUpper=()=>{
        console.log("Uppercase was clicked");
        let newText=text.toUpperCase();
        setText(newText);
        props.showAlert("Converted to Uppercase","success")
      }
    
      const changeToLower=()=>{
        console.log("Lower case was clicked");
        let newText=text.toLowerCase()
        setText(newText);
        props.showAlert("Converted to Lowercase","success")
      }
    
      const clearText=()=>{
        console.log("Clear text was clicked");
        let newText="";
        setText(newText);
        props.showAlert("Text cleared successfully","success")
      }
    
      const reverseTheText=()=>{
        let newText=text.split("").reverse().join("");
        setText(newText);
        props.showAlert("Text reversed successfully","success")
      }
    
      const handleCopy=()=>{
        
        navigator.clipboard.writeText(text);
        props.showAlert("Text copied to clipboard successfully","success")
      }
    
      
    
      const removeSpaces=()=>{
        let newText=text.split(/[ ]+/);
        setText(newText.join(" "));
        props.showAlert("Removed spaces  successfully","success")
      }
  return (
    <div>
        <h2>This is a text analyzer</h2>
        <textarea value={text} onChange={handleOnChange}
        rows="5" style={{width: '100%'}}
        ></textarea>
        <h3>{text}</h3>
        <div className="container my-5" style={{color:props.mode==='dark'?'white':'#042743'}}>
        <h2>Your text Summary</h2>
        <p>{text.split(/\s+/).filter((a1)=>{return a1.length!==0}).length} words and {text.length} characters</p>
        <p>{0.008*text.split(" ").length} Minutes to read</p>
        <h3>Preview</h3>
        <p>{text.length>0?text:"Nothing to preview"}</p>
      </div>
    </div>
  )
}

export default Text
